// DrawChurch at x, y
func DrawChurch(x int, y int) {
	*w4.DRAW_COLORS = 0x4320
	w4.Blit(&church[0], x-churchOffset, y-churchHeight, churchWidth, churchHeight, churchFlags)
}

// DrawChurchTile at x, y
func DrawChurchTile(x int, y int, srcX byte, srcY byte) {
	*w4.DRAW_COLORS = 0x4320

	drawTile(x, y, srcX, srcY, church[:], churchWidth, churchHeight, churchOffset)
}

func drawTile(x int, y int, srcX byte, srcY byte, sprite []byte, spriteWidth, spriteHeight, spriteOffset int) {
	tx, ty := IsoTransform(x, y)

	if x < int(srcX) {
		spriteX := spriteOffset - TileWidth/2 - (int(srcX)-x)*TileWidth/2
		height := spriteHeight - (int(srcX)-x)*TileHeight/2
		w4.BlitSub(&sprite[0], tx+CameraX-TileWidth/2, ty+CameraY-height, TileWidth/2, uint(height), uint(spriteX), 0, spriteWidth, churchFlags)
	} else if y < int(srcY) {
		spriteX := spriteOffset + (int(srcY)-y)*TileWidth/2
		height := spriteHeight - (int(srcY)-y)*TileHeight/2
		w4.BlitSub(&sprite[0], tx+CameraX, ty+CameraY-height, TileWidth/2, uint(height), uint(spriteX), 0, spriteWidth, churchFlags)
	} else {
		spriteX := spriteOffset - TileWidth/2
		w4.BlitSub(&sprite[0], tx+CameraX-TileWidth/2, ty+CameraY-spriteHeight, TileWidth, uint(spriteHeight), uint(spriteX), 0, spriteWidth, churchFlags)
	}
}
